package com.operators;

public class BitwiseOperatorsDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println(" 3 " + " & " + " 2 = " + (3&2));

		System.out.println(" 3 " + " | " + " 2 = " + (3|2));

		System.out.println(" 3 " + " ! " + " 2 = " + (3^2));
	}

}
