package com.operators;

public class RelationalOperatorsDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 5, y = 6;
		
		System.out.println(x + " > " + y + " = " + (x > y));
		System.out.println(x + " < " + y + " = " + (x < y));
		System.out.println(x + " >= " + y + " = " + (x >= y));
		System.out.println(x + " <= " + y + " = " + (x <= y));
		System.out.println(x + " != " + y + " = " + (x != y));
		System.out.println(x + " == " + y + " = " + (x == y));
	}

}
